package com.example.myapplication.handler;import android.annotation.SuppressLint;import android.app.Activity;import android.content.pm.ActivityInfo;import android.os.Handler;import android.os.Message;public class ChangeOrientationHandler extends Handler {    private Activity activity;    public ChangeOrientationHandler(Activity ac) {        super();        activity = ac;    }    @SuppressLint("WrongConstant")    @Override    public void handleMessage(Message msg) {        if (msg.what==888) {            int orientation = msg.arg1;            if (orientation>45&&orientation<135) {                activity.setRequestedOrientation(8);            }else if (orientation>135&&orientation<225){                activity.setRequestedOrientation(9);            }else if (orientation>225&&orientation<315){                activity.setRequestedOrientation(0);            }else if ((orientation>315&&orientation<360)||(orientation>0&&orientation<45)){                activity.setRequestedOrientation(1);            }        }        super.handleMessage(msg);    }}