package com.example.myapplication.utils;import java.util.UUID;/** * 数据处理类 * @author 钟三 * */public class DataUtil {    /**     *  小数点位数处理     * @param digit传入小数点位数     * @return 小数值，用于与原数据相乘即可得到最终值,如0.01     */    public static double RadixPoint(int digit) {        double radixPoint = 1;        switch (digit) {            case 0:                radixPoint = 1;                break;            case 1:                radixPoint = 0.1;                break;            case 2:                radixPoint = 0.01;                break;            case 3:                radixPoint = 0.001;                break;            case 4:                radixPoint = 0.0001;                break;            default:                break;        }        return radixPoint;    }    /**     *	十六进制转换十进制     * @param String 传入十六进制字符     * @return Integer 返回十进制数字     */    public static int hexChangeDecimal(String hex) {        Integer decimal=Integer.parseInt(hex,16);        return decimal;    }    /**     *	十进制转换十六进制     * @param int 十进制数字     * @return String 十六进制字符     */    public static String decimalChangeHex(int decimal) {        String hex = Integer.toHexString(decimal);        return hex;    }    /**UUID     * @return String UUID     */    public static String UUID() {        return UUID.randomUUID().toString().replaceAll("-","");    }}